import React, { Component } from 'react';

interface nft_list_props {
  storage: any;
}
interface nft_member_props {
  id: number;
  storage: any;
}
class NFT_Member extends Component<nft_member_props,nft_member_props>{

  constructor(props: nft_member_props){
    super(props);
    this.state = {
      id: props.id,
      storage: props.storage
    };

  }

  render () {
    return (
        <div>
          <h3>NFT ID: {this.props.id.toString()}</h3>
          <li>
           <span> 
              current stock: {this.props.storage.get(this.props.id.toString())["current_stock"].toString()} 
           </span>
          </li>
          <li>
            <span>
              token price: {this.props.storage.get(this.props.id.toString())["token_price"].toString()}
            </span>
          </li>
          <li>
            <span>
              token address: {this.props.storage.get(this.props.id.toString())["token_address"].toString()}
            </span>
          </li>

        </div>
        )
    }

}
class NFT_List extends Component<nft_list_props,nft_list_props>{

    constructor(props: nft_list_props) {
        super(props);
        this.state = {
          storage: props.storage
        };
    }

    render() {
      const Fragment = React.Fragment;

      var my_nft_list = [];
      if (this.props.storage){
        for(let id=0;;id++) {
          if(!this.props.storage.get(id.toString())) break;
          my_nft_list.push(<NFT_Member id={id} storage={this.props.storage}/>);
          console.log(my_nft_list);
        }
        return my_nft_list;

      } else {
        return (
          <div>
            <span>
              None
            </span>
          </div>
          );

      }
    }
}

export default NFT_List;
