import React, { useState, Dispatch, SetStateAction } from "react";
import { TezosToolkit, WalletContract } from "@taquito/taquito";
import { ToastContainer, toast } from 'react-toastify';

interface RecoverNFTProps {
  contract: WalletContract | any;
  setUserBalance: Dispatch<SetStateAction<any>>;
  Tezos: TezosToolkit;
  userAddress: string;
  setStorage: Dispatch<SetStateAction<any>>;
  setNftList: Dispatch<SetStateAction<any>>;
  setTransactionInProgress: Dispatch<SetStateAction<any>>;
}

const RecoverNFT = ({ contract, setUserBalance, Tezos, userAddress, setStorage, setNftList, setTransactionInProgress}: RecoverNFTProps) => {
  const [loadingBuy, setLoadingBuy] = useState<boolean>(false);
  const [id, setId] = useState<number>();


  const recover_nft = async (): Promise<void> => {
      setLoadingBuy(true);
      setTransactionInProgress(true);
      try {
        const op = await contract.methods.recover(id).send();

        await op.confirmation();
        const newStorage: any = await contract.storage();
        if (newStorage) setStorage(newStorage);
        let newNftList: any = [];
        newStorage.forEach( (value: any, key: number, idx: any) => {
          newNftList.push({id: key, stock:value.current_stock.toNumber(), price:value.token_price.toNumber() /1000000, address:value.token_address, admin: value.token_admin});
        });
        setNftList(newNftList);
        setUserBalance(await Tezos.tz.getBalance(userAddress));
        toast("Successfully recovered your nft", {type:"success"});
      } catch (error) {
        console.log(error);
        toast.error(
          'There was an error sending your transaction. Please check developer console.'
        );
      } finally {
        setTransactionInProgress(false);
        setLoadingBuy(false);
      }

  };


  if (!contract && !userAddress) return <div>&nbsp;</div>;
  return (
    <div>
      &nbsp;

       <br/>
       <br/>
       <br/>
      &nbsp;<span> Enter Nft ID to recover </span>
       <br/>
       <br/>
      &nbsp;<input
        type="number"
        placeholder="NFT ID"
        value={id}
        onChange={e => setId(parseInt(e.target.value,10))}
      />
      &nbsp;<button 
        className="button" 
        onClick={recover_nft}
      >
        {loadingBuy ? (
          <span>
            <i className="fas fa-spinner fa-spin"></i>&nbsp; Please wait
          </span>
        ) : (
          <span>
            <i className="fas fa-plus"></i>&nbsp; Recover NFT
          </span>
        )}
      </button>
    </div>
  );
};

export default RecoverNFT;
