import React, { Component } from 'react';

class NFT_List extends Component {

    constructor(props: nft_list_props) {
        super(props);
        this.state = {
          storage: props.storage
        };
    }

    render() {
      const Fragment = React.Fragment;
      return (
        <div>

        <li>
         <span> 
            current stock: {this.storage.get("0")["current_stock"].toString()} 
         </span>
        </li>
        <li>
          <span>
            token price: {this.storage.get("0")["token_price"].toString()}
          </span>
        </li>
        <li>
          <span>
            token address: {this.storage.get("0")["token_address"].toString()}
          </span>
        </li>
        </div>
      );
    }
}

export default NFT_List;
