import React, { useState, Dispatch, SetStateAction } from "react";
import { TezosToolkit, WalletContract } from "@taquito/taquito";

interface BuyNFTProps {
  contract: WalletContract | any;
  setUserBalance: Dispatch<SetStateAction<any>>;
  Tezos: TezosToolkit;
  userAddress: string;
  setStorage: Dispatch<SetStateAction<any>>;
  setNftList: Dispath<SetStateAction<any>>;
}

const BuyNFT = ({ contract, setUserBalance, Tezos, userAddress, setStorage, setNftList}: BuyNFTProps) => {
  const [loadingBuy, setLoadingBuy] = useState<boolean>(false);
  const [id, setId] = useState<number>(0);

  // const [loadingDecrement, setLoadingDecrement] = useState<boolean>(false);

  const buy_nft = async (): Promise<void> => {
      setLoadingBuy(true);
      try {
        const op = await contract.methods.default(id).send(
          {
            from: userAddress,
            amount: nftList[id].token_price
          });
        console.log(contract.methods);
        await op.confirmation();
        const newStorage: any = await contract.storage();
        if (newStorage) setStorage(newStorage);
        let nftList = [];
        newStorage.forEach( (value,key,idx) => {
          nftList.push({});
        });
        setNftList(nftList);
        setUserBalance(await Tezos.tz.getBalance(userAddress));
      } catch (error) {
        console.log(error);
      } finally {
        setLoadingBuy(false);
      }

  };

/*
  const increment = async (): Promise<void> => {
    setLoadingIncrement(true);
    try {
      const op = await contract.methods.increment(1).send();
      await op.confirmation();
      const newStorage: any = await contract.storage();
      if (newStorage) setStorage(newStorage.toNumber());
      setUserBalance(await Tezos.tz.getBalance(userAddress));
    } catch (error) {
      console.log(error);
    } finally {
      setLoadingIncrement(false);
    }
  };

  const decrement = async (): Promise<void> => {
    setLoadingDecrement(true);
    try {
      const op = await contract.methods.decrement(1).send();
      await op.confirmation();
      const newStorage: any = await contract.storage();
      if (newStorage) setStorage(newStorage.toNumber());
      setUserBalance(await Tezos.tz.getBalance(userAddress));
    } catch (error) {
      console.log(error);
    } finally {
      setLoadingDecrement(false);
    }
  };
*/

  if (!contract && !userAddress) return <div>&nbsp;</div>;
  return (
    <div className="buttons">
      <input
        type="number"
        placeholder="NFT ID"
        value={id}
        onChange={e => setId(parseInt(e.target.value,10))}
      />
      <button 
        className="button" 
        onClick={buy_nft}
      >
        {loadingBuy ? (
          <span>
            <i className="fas fa-spinner fa-spin"></i>&nbsp; Please wait
          </span>
        ) : (
          <span>
            <i className="fas fa-plus"></i>&nbsp; Buy NFT
          </span>
        )}
      </button>
    </div>
  );
};

export default BuyNFTProps;
