import React, { Component } from 'react';

interface nft_list_props {
  storage: any;
}
interface nft_member_props {
  member: any;
}
class NFT_Member extends Component<nft_member_props,nft_member_props>{

  constructor(props: nft_member_props){
    super(props);
    this.state = {
        member: props.member
    };

  }

  render () {
    return (
        <div>
          <li>
           <span> 
              current stock: {this.props.member["current_stock"].toString()} 
           </span>
          </li>
          <li>
            <span>
              token price: {this.props.member["token_price"].toString()}
            </span>
          </li>
          <li>
            <span>
              token address: {this.props.member["token_address"].toString()}
            </span>
          </li>

        </div>
        )
    }

}
class NFT_List extends Component<nft_list_props,nft_list_props>{

    constructor(props: nft_list_props) {
        super(props);
        this.state = {
          storage: props.storage
        };
    }

    render() {
      const Fragment = React.Fragment;

      var my_nft_list = [];
      if (this.props.storage){
        let keys = this.props.storage.keys();
        let idx = keys.next().value;
        while (idx) {
          console.log(idx);

          my_nft_list.push(
                      <div>
                      <h4>
                        NFT ID: {idx.toString()}
                      </h4>
                      <NFT_Member member={this.props.storage.get(idx.toString())} />
                      </div>
                      );
          idx = keys.next().value;

        }
        return my_nft_list;

      } else {
        return (
          <div>
            <span>
              None
            </span>
          </div>
          );

      }
    }
}

export default NFT_List;
